package linkList;
/**
 * 链表节点
 * @author linziyu
 *
 */


public class ListNode {
		private int data;
		private ListNode next;
		public ListNode(int data) {
			// TODO Auto-generated constructor stub
			this.data = data;
		}
		public int getData() {
			return data;
		}
		public void setData(int data) {
			this.data = data;
		}
		public ListNode getNext() {
			return next;
		}
		public void setNext(ListNode next) {
			this.next = next;
		}
		
		
		
}
